<?php class Mail{public static function send($destinataire, $sujet, $mail){$email_expediteur='contact@furiouscreation.com';$email_reply=$email_expediteur;$frontiere = '-----=' . md5(uniqid(mt_rand()));$headers = "MIME-Version: 1.0" . "\r\n";$headers .= "Content-type:text/html;charset=iso-8859-1" . "\r\n";$headers .= 'From: "Furious Creation" <'.$email_expediteur.'>'."\r\n";$headers .= 'Return-Path: <'.$email_reply.'>'."\r\n";$message = 'This is a multi-part message in MIME format.'."\n\n";$message .= '--'.$frontiere."\n";$message .= 'Content-Type: text/plain; charset="iso-8859-1"'."\n";$message .= 'Content-Transfer-Encoding: 8bit'."\n\n";$message .= $message_texte."\n\n";$message .= '--'.$frontiere."\n";$message .= 'Content-Type: text/html; charset="utf-8"'."\n";$message .= 'Content-Transfer-Encoding: 8bit'."\n\n";$message .= $message_html."\n\n";$message .= '--'.$frontiere."\n";$mail = '<html><head></head><body style="background-color:#eee; font-family:arial; font-size:13px;"><div style="max-width:550px; margin:30px auto;"><p style="text-align:center;"><img src="http://www.furiouscreation.com/img/logo-furious-creation.png" /></p><div style="background:#fff; margin-bottom:10px; padding:10px; border-radius:3px; border-bottom:3px solid #ddd; overflow:hidden;">'.$mail.'</div></div></body></html>';if(mail($destinataire, $sujet, $mail, $headers)){ return TRUE;}else{ return FALSE;}}}?>