<?php include("../config/config.php");    checkOutSession();if(isset($_POST['submit_project_form'])){if(isset($_POST['id_project']) && is_numeric($_POST['id_project'])){$id_project = Project::update($_POST); }else{$id_project = Project::add($_POST); }header('Location: ../admin/?page=project&id='.$id_project);exit;}elseif(isset($_POST['action']) && 'remove'==$_POST['action'] && is_numeric($_POST['id'])){$deleted = Project::remove($_POST['id']);if($deleted) echo 'done';exit;}if(isset($_POST['submit_detail_form'])){if(isset($_POST['id_detail']) && is_numeric($_POST['id_detail'])){$id = Project::updateDetail($_POST); }else{$id = Project::addDetail($_POST); }header('Location: ../admin/?page=project_detail&idp='.$_POST['id_project'].'&id='.$id);exit;}elseif(isset($_POST['action']) && 'remove_detail'==$_POST['action'] && is_numeric($_POST['id'])){$deleted = Project::removeDetail($_POST['id']);if($deleted) echo 'done';exit;}header('Location: ../admin/');exit;?>